'use strict'

var transpose = require('../note/transpose')
var split = require('./splitName')

/**
 * Given a dictionary return a function to get the notes or intervals from it
 *
 * @name dictionary.getter
 * @function
 * @param {Hash} dictionary - the set dictionary
 * @return {Function} a function to get the notes or intervals from the set dictionary
 * @see scale.get
 * @see chord.get
 *
 * @example
 * var d = dictionary({'Maj7': ['1 3 5 7'], 'm7': ['1 3b 5 7b'] })
 * var get = getter(d)
 * get('CMaj7') // => ['C', 'E', 'G', 'B']
 */
module.exports = function (dictionary) {
  return function get (name, tonic) {
    var n = split(name)
    if (n.length === 2) return get(n[0], tonic || n[1])
    else if (arguments.length === 1) return function (t) { return get(name, t) }
    var v = dictionary[name]
    return v ? v.intervals.map(transpose(tonic)) : []
  }
}

'use strict'

var binary = require('../set/binary')
var split = require('../gamut/split')

/**
 * Get a function to perform an inverse dictionary lookup (given notes, return names)
 *
 * @name dictionary.names
 * @function
 * @param {Array} names - the list of all names
 * @param {Hash} dictionary - the dictionary
 * @param {Function} builder - the name builder
 * @return {Function} a function to perform inverse lookup
 * @see scale.names
 * @see chord.names
 */
module.exports = function (names, dictionary, sep) {
  return function (notes) {
    if (arguments.length === 0) return names.slice()
    var s = dictionary[binary(notes)]
    if (!s) return []
    var tonic = split(notes)[0]
    return [ s.name ].concat(s.aliases).map(function (name) {
      return tonic + sep + name
    })
  }
}

