var curry = require('../_curry')
var split = require('./split')

/**
 * Select some elements from a gamut
 *
 * @name gamut.select
 * @function
 * @param {String|Array} numbers - a __1-based__ index of the elements
 * @param {String|Array} gamut - the notes or intervals
 * @return {Array} the selected elements
 *
 * @example
 * var select = require('music.kit/gamut/select')
 * select('1 3 5', 'C D E F G A B') // => ['C', 'E', 'G']
 */
module.exports = curry(function (nums, gamut) {
  var g = split(gamut)
  return split(nums).map(function (n) {
    return g[n - 1]
  })
})

'use strict'

var height = require('../pitch/height')
var operation = require('./operation')

/**
 * Get a gamut in ascdening pitch order
 *
 * @name gamut.sort
 * @function
 * @param {String|Array} gamut - the gamut to sort
 * @return {Array} the gamut in sort pitch order
 *
 * @example
 * var sort = require('music.kit/gamut.sort')
 * sort('c5 d2 f4 D2') // => ['D2', 'D2', 'F4', 'C5']
 */
module.exports = operation(function (gamut) {
  return gamut.sort(function (a, b) {
    return height(a) - height(b)
  })
})

'use strict'

var props = require('../pitch/props')
var cache = {}

/**
 * Convert from [array interval notation](https://github.com/danigb/music.array.notation)
 * to [shorthand interval notation](https://en.wikipedia.org/wiki/Interval_(music)#Shorthand_notation)
 *
 * The returned string has the form: `number + quality` where number is the interval number
 * (positive integer for ascending intervals, negative integer for descending intervals, never 0)
 * and the quality is one of: 'M', 'm', 'P', 'd', 'A' (major, minor, perfect, dimished, augmented)
 *
 * @name interval.str
 * @function
 * @param {Array} interval - the interval in array notation
 * @return {String} the interval string in shorthand notation or null if not valid interval
 *
 * @example
 * interval.str([1, 0, 0]) // => '2M'
 * interval.str([1, 0, 1]) // => '9M'
 */
module.exports = function (arr) {
  if (!Array.isArray(arr) || arr.length !== 2) return null
  var str = '|' + arr[0] + '|' + arr[1]
  return str in cache ? cache[str] : cache[str] = build(arr)
}

var ALTER = {
  P: ['dddd', 'ddd', 'dd', 'd', 'P', 'A', 'AA', 'AAA', 'AAAA'],
  M: ['ddd', 'dd', 'd', 'm', 'M', 'A', 'AA', 'AAA', 'AAAA']
}
var TYPES = 'PMMPPMM'

function build (coord) {
  var p = props(coord)
  var t = TYPES[p[0]]

  var dir, num, alt
  // if its descening, invert number
  if (p[2] < 0) {
    dir = -1
    num = (8 - p[0]) - 7 * (p[2] + 1)
    alt = t === 'P' ? -p[1] : -(p[1] + 1)
  } else {
    dir = 1
    num = p[0] + 1 + 7 * p[2]
    alt = p[1]
  }
  var q = ALTER[t][4 + alt]
  return dir * num + q
}

